def afficher(colonnes, option = 1):
    if option == 1:
        SYMBOLES = ['⬜', '⭕', '⚫']
        print(" 0 1  2 3  4 5  6\n"+"".join(["".join([SYMBOLES[colonnes[x][y]] for x in range(7)])+'\n' for y in reversed(range(6))]))
    else:
        SYMBOLES = [" - ", " x ", " o "]
        print(" 0  1  2  3  4  5  6\n"+"".join(["".join([SYMBOLES[colonnes[x][y]] for x in range(7)])+'\n' for y in reversed(range(6))]))

